import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import ReactDOM from 'react-dom'
import React, { Component, Fragment } from 'react';
import axiosInstance from '../axios';
import Loader from '../Loader'
import CommentsList from '../components/CommentsList'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faComment, faThumbsDown, faThumbsUp } from '@fortawesome/free-regular-svg-icons'
import { faReply } from '@fortawesome/free-solid-svg-icons';


class Post extends Component {
    state = {
        post: '',
        comments: '',
        loading_post: true,
        loading_comments: true
    };

    componentDidMount(){        
        const id = this.props.match.params.id;
        this.getPost(id)
    }

    getPost(id){
        axiosInstance.get(`http://localhost:8000/api/posts/${id}`)
        .then(res => {
            this.setState({
                post: res.data,
                loading_post: false
            });
            console.log(this.state.post)
            this.getComments(id);
        })
    }

    getComments(id){
        axiosInstance.get(`http://localhost:8000/api/posts/comments/${id}`)
        .then(res => {
            this.setState({
                comments: res.data,
                loading_comments: false
            });
            console.log('COMMENTS', this.state.comments)
        })
    }

  formatDate(str){
        let date = new Date(str);
        return date.getHours() + ':' + (date.getMinutes().length == 1 ? '0' + date.getMinutes() : date.getMinutes()) + ' · ' + 
               date.getFullYear()+ '-' + (date.getMonth()+1) + '-' + date.getDate();   
    }

    getRandomInt(max) {
        return Math.floor(Math.random() * Math.floor(max));
      }

    render(){
        let post = this.state.loading_post ? <Loader /> : 
        <div>
            <article className="posts-container pt-3 pl-3 pr-3 pb-0">
                <div className="d-flex">
                    <img className="rounded-circle" src={this.state.post.user.img} height="50" width="50"></img>
                    <div className="ml-2">
                        <p className="m-0"><b>{this.state.post.user.user.first_name} {this.state.post.user.user.last_name}</b></p>
                        <p style={{color:'#5b7083'}} className="m-0">@{this.state.post.user.user.username}</p>
                    </div>
                </div>
                <div>
                    <p style={{fontSize: "18pt"}} className="mt-2 mb-1" >{this.state.post.body}</p>
                    <p className="mb-3" style={{color:'#5b7083', fontSize: "12pt"}}> {this.formatDate(this.state.post.date)}</p>
                    <hr className="mt-3 mb-3"/>
                    <p className="mb-3" style={{color: "#5b7083"}}> 
                        <span><b style={{color: "#212529"}}>{this.state.post.likes.length}</b> likes</span> 
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.state.post.dislikes.length}</b> dislikes</span>
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.getRandomInt(1000)}</b> comments</span>
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.getRandomInt(100)}</b> reposts</span>
                    </p>
                    <hr className="mt-3 mb-3"/>
                    <div className='d-flex justify-content-around' style={{color: "#5b7083"}}>
                        <p><span className="post-icon like-post"><FontAwesomeIcon icon={faThumbsUp} size="lg"/></span></p>
                        <p><span className="post-icon dislike-post"><FontAwesomeIcon icon={faThumbsDown} size="lg"/></span></p>
                        <p><span className="post-icon repost-post"><FontAwesomeIcon icon={faComment} size="lg"/></span></p>
                        <p><span className="post-icon repost-post"><FontAwesomeIcon icon={faReply} size="lg"/></span></p>
                    </div>
                </div>
            </article>
            <div>
                <CommentsList comments={this.state.post.comments}></CommentsList>
            </div>
        </div>
        return(
        <Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-7">
                        <h5 className="header">Post</h5>
                        {post}
                    </div>
                </div>
            </div>
        </Fragment>
    )
  }
}

export default Post
