import React, { Component, useState } from 'react';
import {BrowserRouter, Redirect, Router, Route, Switch} from 'react-router-dom'

import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { createBrowserHistory } from 'history';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


class AddPost extends Component{
  constructor(props) {
    super(props);
    this.state = {
      body: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(e){
    e.preventDefault();
    console.log(this.props.user_id);
    fetch('http://localhost:8000/api/posts/new/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        user: this.props.user_id,
        body: this.state.body
      })
    })
    .then(this.props.getPosts())
  }

  handleChange = e => {
    const name = e.target.name;
    const value = e.target.value;
    console.log(name, value);
    this.setState(prevstate => {
      const newState = { ...prevstate };
      newState[name] = value;
      return newState;
    });
  };

  componentDidMount(){
    console.log(this.props.user_id);
  }

  render(){
    return (
            <div className="pt-3 pb-3 home-container">
                <h6>Say something!</h6>
                <form onSubmit={this.handleSubmit}>
                    <textarea onChange={this.handleChange} className="w-100" name="body"></textarea>
                    <div className="post-btns row justify-content-between">
                      <div>
                      <FontAwesomeIcon icon={['fa', 'home']} />
                      </div>
                      <div>
                        <button type='submit' className="btn btn-primary">Post</button>
                      </div>

                    </div>
                </form> 
            </div>
    );
  }
}

export default AddPost;
