import React from 'react';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';

class LoginForm extends React.Component {
  state = {
    username: '',
    password: ''
  };

  handle_change = e => {
    const name = e.target.name;
    const value = e.target.value;
    this.props.updateState(value);
    this.setState(prevstate => {
      const newState = { ...prevstate };
      newState[name] = value;
      return newState;
    });
  };

  handle_login = (e, data) => {
    e.preventDefault();
    fetch('http://localhost:8000/api/token-auth/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(res => res.json())
      .then(json => {
        console.log(json);
        localStorage.setItem('access_token', json.access);
        localStorage.setItem('refresh_token', json.refresh);
        // this.setState({
        //   logged_in: true,
        //   displayed_form: '',
        // });
        this.props.updateState(true, '')
        this.getCurrentUser();
        this.history.push('/')
      })
  };

  render() {
    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
              <h2>Welcome to <strong>Microblog!</strong></h2>
            </div>
            <div className="row justify-content-center mt-3">
              <h3>Sign In</h3>
            </div>
            <div className="row justify-content-center">
                <form className="col-8 col-lg-4 col-md-6 col-sm-8" onSubmit={e => this.props.handle_login(e, this.state)}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Username</label>
                        <input onChange={this.handle_change} type="text" className="form-control" id="username" name="username" aria-describedby="usernameHelp" placeholder="Enter username" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword1">Password</label>
                        <input onChange={this.handle_change} type="password" className="form-control" id="password" name="password" placeholder="Password" />
                    </div>
                    <button type="submit" className="btn btn-primary w-100">Sign In</button>
                </form>
            </div>
            <div className="row justify-content-center mt-3">
              <p>Please, log in or <i><NavLink to={"/register"}>create an account!</NavLink></i></p>
            </div>
        </div>
    );
  }
}

export default LoginForm;

