import React, {Component } from 'react'
import PropTypes from 'prop-types'

class PostItem extends Component{
    constructor(props) {
      super(props);
      this.state = {

      };
      this.post = props.post;
    }

    formatDate(date){
      let dateObj = new Date(date)
      let dateNow = new Date();
      let timeDiff = Math.abs(dateNow.getTime() - dateObj.getTime())/1000;
      let add;
      if (timeDiff < 60)
        add = ' seconds'
      else if (timeDiff < 60 * 60){
        add = ' minutes'
        timeDiff /= 60
      }
      else if (timeDiff < 60 * 60 * 24){
        add = ' hours'
        timeDiff /= 60 * 24
      }
      else if (timeDiff < 60 * 60 * 24 * 30){
        add = ' days'
        timeDiff /= 60 * 60 * 24
      }

      let res = Math.round(timeDiff) ? add : 'sex' 
      return res
    }

    render(){
    return (
    <article className="post-container">
      <div className="d-inline-flex flex-wrap">
        <p><b>{this.post.user.first_name} {this.post.user.last_name}</b></p>
        <p style={{color:"gray"}}>&nbsp;@{this.post.user.username}</p>
        <p style={{color:"gray"}}>&nbsp;&middot;&nbsp;{this.formatDate(this.post.date)}</p>
      </div>

        <div>{this.post.body}</div>

    </article>
    )
}
}

export default PostItem
