import { useHistory } from 'react-router-dom';
import { useState } from 'react';
import React, {Fragment} from 'react'

export default function SignUp(){
    const history = useHistory();
    const initialFormData = Object.freeze({
        username: '',
        password: '',
    });

    const [formData, updateFormData] = useState(initialFormData);

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim(),
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);

        // axiosInstance
        //     .post(`users/`, {
        //         username: formData.username,
        //         password: formData.password,
        //     }).then((res) => {
        //         history.push('/login');
        //         console.log(res);
        //         console.log(res.data);
        //     });

        fetch('http://localhost:8000/api/create/', {
            method: 'POST',
            body: JSON.stringify({
                username: formData.username,
                password: formData.password,
                email: "test@gmail.com",
            }),
            headers: {
                'Content-Type':'application/json',
                accept: 'application/json',
            }
        }).then((res) => {
            history.push('/login');
            console.log(res);
            console.log(res.data);
        })   
    }     

    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
              <h2>Welcome to <strong>Microblog!</strong></h2>
            </div>
            <div className="row justify-content-center mt-3">
                <h3>Sign Up</h3>
            </div>
            <div className="row justify-content-center">
                <form className="col-8 col-lg-4 col-md-6 col-sm-8">
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Username</label>
                        <input onChange={handleChange} type="text" className="form-control" id="username" name="username" aria-describedby="usernameHelp" placeholder="Enter username" />
                        <small id="usernameHelp" className="form-text text-muted">Create your unique username</small>
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputPassword1">Password</label>
                        <input onChange={handleChange} type="password" className="form-control" id="password" name="password" placeholder="Password" />
                    </div>
                    <button onClick={handleSubmit} type="submit" className="btn btn-primary w-100">Sign Up</button>
                </form>
            </div>
        </div>
    )
}
