import React,  {Component, Fragment} from 'react'
import Recommend from '../components/Recommend'
import Loader from '../Loader'
import axiosInstance from 'axios';


class Search extends Component{
    constructor(props) {
      super(props);
      this.state = {
        loading: true, 
        query: ''
      };
    }

    componentDidMount(){
        let param = this.getParam();
        this.search(param);
    }

    getParam(){
        const params = new URLSearchParams(this.props.location.search);
        const param = params.get('query');
        return param
    }

    search(param){
        console.log(this.state.query);
        axiosInstance.post(`http://localhost:8000/api/search/`, {
            query: param,
        })
        .then(response => {
            this.setState({
                loading: false
            })
            console.log(response)
        })
    }

    render(){
        return(
            <Fragment>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-10 col-lg-7">
                            <h5 className="header">Search</h5>
                            <div className="home-container">
                                {/* <form className="p-3">
                                    <div className="form-row align-items-center">
                                        <div class="col-sm-10 my-1">
                                            <input type="text" className="form-control" id="search" name="search" placeholder="Enter search query" />
                                        </div>

                                        <div class="col-auto my-1">
                                            <button type="submit" className="def-btn btn-normal">Search</button>
                                        </div>
                                    </div>
                                </form> */}
                                <form class="form-inline">
                                    <div class="form-group mb-2">
                                        <label for="staticEmail2" class="sr-only">Email</label>
                                        <input type="text" readonly class="form-control-plaintext" id="staticEmail2" value="email@example.com" />
                                    </div>
                                    <div class="form-group mx-sm-3 mb-2">
                                        <label for="inputPassword2" class="sr-only">Password</label>
                                        <input type="password" class="form-control" id="inputPassword2" placeholder="Password" />
                                    </div>
                                    <button type="submit" class="btn btn-primary mb-2">Confirm identity</button>
                                </form>
                            </div>

                            {this.state.loading && <Loader />}
                        </div>
                        <div className="ml-0 mb-4 d-none d-xl-block d-lg-block d-md-none col-lg-4">
                            <div className=" side-container sticky-top">
                                <Recommend content={'users'}/>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }

}

export default Search
