import React,  {Component, Fragment} from 'react'
import AddPost from '../components/AddPost'
import PostsList from '../components/PostsList'
import Loader from '../Loader'

import { faHome } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class Home extends Component{
    constructor(props) {
      super(props);
      this.state = {
        posts:[],
        loading: true
      };
      this.getPosts = this.getPosts.bind(this)
    }

    componentDidMount(){
        this.getPosts()
    }

    getPosts(){
        fetch('http://localhost:8000/api/posts/')
        .then(response => response.json())
        .then(posts => {
            this.setState({
                posts: posts,
                loading: false
            })
        })
    }

    render(){
    return(
        <Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-lg-3">
                        <p>ЗДЕСЬ МОГЛА БЫТЬ ВАША РЕКЛАМА</p>
                    </div>
                    <div className="col-md-10 col-lg-7">
                        <h5 className="header">Home page</h5>
                        <AddPost user_id={this.props.user_id} getPosts={this.getPosts}></AddPost>
                        <PostsList user_id={this.props.user_id} posts={this.state.posts}></PostsList>
                        {this.state.loading && <Loader />}
                    </div>
                    <div className="col-lg-2">
                        <p>ЗДЕСЬ ТОЖЕ МОГЛА БЫТЬ ВАША РЕКЛАМА НО ПОМЕНЬШЕ</p>
                    </div>
                </div>
            </div>
        </Fragment>
    )
    }
}

export default Home;
