import React,  {Component, Fragment} from 'react'
import PostsList from '../components/PostsList'
import Loader from '../Loader'

class Profile extends Component{
    constructor(props) {
      super(props);
      this.state = {
       user: '',
       posts: [],
       loading: true,
      };
    }

    componentDidMount(){
        fetch('http://localhost:8000/api/current_user/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(user => {
            this.setState({user:user});
            this.getPosts();
        })
    }

    getPosts(){
        console.log(this.state.user)
        fetch(`http://localhost:8000/api/posts/user/${this.state.user.id}`, {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(posts => {
            this.setState({
                posts: posts,
                loading: false
            });
        })
    }


    render(){
        return(
            <Fragment>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-10 col-lg-7">
                            <h5 className="header">{this.state.user.first_name} {this.state.user.last_name} 
                                <br /><span style={{color:"gray", fontSize:"12pt", fontWeight: "normal"}}>{this.state.posts.length} posts </span>
                            </h5>
                            <div className="pt-1 pb-1 home-container">
                           <div className="d-flex align-items-center justify-content-between">
                               <div>
                                    <img className="mr-2" src="https://fogsoft.com/wp-content/uploads/2017/07/user-logo.png" width="150"></img>
                               </div>
                               <div>
                                   <p>{this.state.user.first_name} {this.state.user.last_name} </p>
                                   <p>{this.state.user.username} </p>
                                   <p>{this.state.user.email} </p>
                                   <button className="btn btn-outline-info">Change profile</button>
                               </div>
                           </div>
                            <h6 style={{padding: "0px 15px"}}>
                                Posts by&nbsp;
                                <b>{this.state.user.first_name} {this.state.user.last_name}</b>&nbsp;
                                <span style={{color: "gray"}}>@{this.state.user.username}</span>
                            </h6>
                            </div>

                            <PostsList user_id={this.props.user_id} posts={this.state.posts}></PostsList>
                            {this.state.loading && <Loader />}
                        </div>
                    </div>
                </div>
            </Fragment>
        )
        }
    }

export default Profile;
