import React,  {Component, Fragment} from 'react'
import PostsList from '../components/PostsList'
import Loader from '../Loader'

class Profile extends Component{
    constructor(props) {
      super(props);
      this.state = {
       user: '',
       posts: [],
       loading: true,
      };
    }

    componentDidMount(){
        fetch('http://localhost:8000/api/current_user/', {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(user => {
            this.setState({user:user});
            this.getPosts();
        })
    }

    getPosts(){
        console.log(this.state.user)
        fetch(`http://localhost:8000/api/posts/user/${this.state.user.id}`, {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(posts => {
            this.setState({
                posts: posts,
                loading: false
            });
        })
    }


    render(){
        return(
            <Fragment>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-10 col-lg-7">
                            <h5 className="header">{this.state.user.first_name} {this.state.user.last_name} <br /><span>{this.state.posts.length} posts </span></h5>

                            <div className="pt-3 pb-1 home-container">

                            <h6 style={{padding: "0px 15px"}}>
                                Posts by&nbsp;
                                <b>{this.state.user.first_name} {this.state.user.last_name}</b>&nbsp;
                                <span style={{color: "gray"}}>@{this.state.user.username}</span>
                            </h6>
                            </div>

                            <PostsList user_id={this.props.user_id} posts={this.state.posts}></PostsList>
                            {this.state.loading && <Loader />}
                        </div>
                    </div>
                </div>
            </Fragment>
        )
        }
    }

export default Profile;
