import axiosInstance from '../axios';
import { useHistory } from 'react-router-dom';
import { useState } from 'react';
import React, {Fragment} from 'react'

export default function SignUp(){
    const history = useHistory();
    const initialFormData = Object.freeze({
        username: '',
        password: '',
    });

    const [formData, updateFormData] = useState(initialFormData);

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim(),
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);

        axiosInstance
            .post(`token/`, {
                username: formData.username,
                password: formData.password,
            }).then((res) => {
                localStorage.setItem('access_token', res.data.access);
                localStorage.setItem('refresh_token', res.data.refresh);
                axiosInstance.defaults.headers['Authorization'] =
                    'JWT ' + localStorage.getItem('access_token');
                history.push('/');
            });
    }     

    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
                <h2>Sign In</h2>
            </div>
            <div className="row justify-content-center">
                <form className="col-4 mt-5">
                    <div className="form-group">
                        <label for="exampleInputEmail1">Username</label>
                        <input onChange={handleChange} type="text" className="form-control" id="username" name="username" aria-describedby="usernameHelp" placeholder="Enter username" />
                        <small id="usernameHelp" className="form-text text-muted">Create your unique username</small>
                    </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input onChange={handleChange} type="password" className="form-control" id="password" name="password" placeholder="Password" />
                    </div>
                    <button onClick={handleSubmit} type="submit" className="btn btn-primary w-100">Sign Up</button>
                </form>
            </div>
        </div>
    )
}
