import React, {Component } from 'react'
import PropTypes from 'prop-types'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { faThumbsUp as likeReg, faThumbsDown as dislikeReg, faComment} from '@fortawesome/free-regular-svg-icons'
import { faReply, faThumbsUp as likeSol, faThumbsDown as dislikeSol} from '@fortawesome/free-solid-svg-icons'

class PostItem extends Component{
    constructor(props) {
      super(props);
      this.state = {

      };
      this.post = props.post;
      this.btnClick = this.btnClick.bind(this)
    }

    formatDate(date){
      let dateObj = new Date(date)
      let dateNow = new Date();
      let timeDiff = Math.abs(dateNow.getTime() - dateObj.getTime())/1000;
      let add;
      if (timeDiff < 60)
        add = ' seconds'
      else if (timeDiff < 60 * 60){
        add = ' minutes'
        timeDiff /= 60
      }
      else if (timeDiff < 60 * 60 * 24){
        add = ' hours'
        timeDiff /= 60 * 60
      }
      else if (timeDiff < 60 * 60 * 24 * 30){
        add = ' days'
        timeDiff /= 60 * 60 * 24
      }

      let res = add ? Math.round(timeDiff) + add : 'More than month ago' 
      return res
    }

    getRandomInt(max) {
      return Math.floor(Math.random() * Math.floor(max));
    }

    btnClick = e => {
      console.log(e.target.getAttribute('name'), 'user:', this.props.user_id, 'post:', this.post.id)
    }

    checkLikes(){
      return true
      // this.post.likes.forEach(user => {
      //   console.log(user.id, user.username)
      //   if (this.props.user_id == user.id){
      //     return true
      //   }
      // })

    }

    render(){
    return (
    <article className="post-container d-flex">
      <div>
        <img className="mr-2" src="https://fogsoft.com/wp-content/uploads/2017/07/user-logo.png" width="50"></img>
      </div>
      <div>
        <div className="d-inline-flex flex-wrap">
          <p className="m-0"><b>{this.post.user.first_name} {this.post.user.last_name}</b></p>
          <p className="m-0" style={{color:"gray"}}>&nbsp;@{this.post.user.username}</p>
          <p className="m-0" style={{color:"gray"}}>&nbsp;&middot;&nbsp;{this.formatDate(this.post.date)}</p>
        </div>
        <div>{this.post.body}</div>
        <div className="d-inline-flex flex-wrap" style={{fontSize:"10pt"}}>
          <p className="m-0" style={{color:"#17bf63"}}> <span name="like" onClick={this.btnClick} className="like-span">Одобряю <FontAwesomeIcon name="like" icon={this.checkLikes() ? likeSol : likeReg}/></span>{this.post.likes.length || ''} </p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#e0245e"}}> <span name="dislike" onClick={this.btnClick} className="dislike-span">Осуждаю <FontAwesomeIcon name="dislike" icon={dislikeReg}/></span>{this.getRandomInt(1000)}</p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#007bff"}}> <span name="comment" onClick={this.btnClick} className="repost-span">Comment <FontAwesomeIcon name="comment" icon={faComment}/></span>{this.getRandomInt(1000)}</p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#007bff"}}> <span name="repost" onClick={this.btnClick} className="repost-span">Repost <FontAwesomeIcon name="repost" icon={faReply}/></span>{this.getRandomInt(1000)}</p>

        </div>      
      </div>  
    </article>
    )
}
}

export default PostItem
