import React,  {Component, Fragment} from 'react'
import Recommend from '../components/Recommend'
import Loader from '../Loader'
import axiosInstance from 'axios';


class Search extends Component{
    constructor(props) {
      super(props);
      this.state = {
        loading: true, 
        query: ''
      };
    }

    componentDidMount(){
        let param = this.getParam();
        this.search(param);
    }

    getParam(){
        const params = new URLSearchParams(this.props.location.search);
        const param = params.get('query');
        return param
    }

    search(param){
        console.log(this.state.query);
        axiosInstance.post(`http://localhost:8000/api/search/`, {
            query: param,
        })
        .then(response => {
            this.setState({
                loading: false
            })
            console.log(response)
        })
    }

    render(){
        return(
            <Fragment>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-10 col-lg-7">
                            <h5 className="header">Search</h5>
                            <div className="home-container">
                                <form className="m-2">
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Username</label>
                                        <input type="text" className="form-control" id="username" name="username" aria-describedby="usernameHelp" placeholder="Enter username" />
                                    </div>
                                </form>
                            </div>

                            {this.state.loading && <Loader />}
                        </div>
                        <div className="ml-0 mb-4 d-none d-xl-block d-lg-block d-md-none col-lg-4">
                            <div className=" side-container sticky-top">
                                <Recommend content={'users'}/>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }

}

export default Search
