import { useHistory } from 'react-router-dom';
import { useState } from 'react';
import React, {Fragment} from 'react'
import axiosInstance from '../axios';

class Register extends Component{
    constructor(props) {
      super(props);

      this.state = {
        error: ''
      };
    }


    handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim(),
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        axiosInstance
			.post(`http://localhost:8000/api/create/`, {
				username: formData.username,
                password: formData.password,
                email: formData.email,
                first_name: formData.firstname,
                last_name: formData.lastname
			})
            .then((res) => {
                history.push('/login');
            })   
    }     

    render(){
        return (
            <div className="container">
                <div className="row justify-content-center mt-5">
                <h2>Welcome to <strong>Microblog!</strong></h2>
                </div>
                <div className="row justify-content-center mt-3">
                    <h3>Sign Up</h3>
                </div>
                <div className="row justify-content-center">
                    <form className="col-8 col-lg-4 col-md-6 col-sm-8 mb-5">
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input onChange={handleChange} type="text" className="form-control" id="username" name="username" aria-describedby="usernameHelp" placeholder="Enter username" />
                            <small id="usernameHelp" className="form-text text-muted">Create your unique username</small>
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input onChange={handleChange} type="email" className="form-control" id="email" name="email" aria-describedby="emailHelp" placeholder="Enter email" />
                            <small id="emailHelp" className="form-text text-muted">Your email</small>
                        </div>
                        <div className="form-group">
                            <label htmlFor="firstname">Firstname</label>
                            <input onChange={handleChange} type="text" className="form-control" id="firstname" name="firstname" aria-describedby="firstnameHelp" placeholder="Enter firstname" />
                            <small id="firstnameHelp" className="form-text text-muted">Your firstname</small>
                        </div>
                        <div className="form-group">
                            <label htmlFor="lastname">Lastname</label>
                            <input onChange={handleChange} type="text" className="form-control" id="lastname" name="lastname" aria-describedby="lastnameHelp" placeholder="Enter lastname" />
                            <small id="lastnameHelp" className="form-text text-muted">Your lastname</small>
                        </div>
                        <div className="form-group">
                            <label htmlFor="exampleInputPassword1">Password</label>
                            <input onChange={handleChange} type="password" className="form-control" id="password" name="password" placeholder="Password" />
                        </div>
                        <button onClick={handleSubmit} type="submit" className="def-btn btn-normal btn-block">Sign Up</button>
                    </form>
                </div>
                <div className="row justify-content-center mt-3">
                <p style={{ color: '#d53d3dd0'}}><b>{this.state.error}</b></p>
                </div>
            </div>
    )}
}

export default Register;
