import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import ReactDOM from 'react-dom'
import React, { Component, Fragment } from 'react';
import axiosInstance from '../axios';
import Loader from '../Loader'
import { faThumbsUp } from '@fortawesome/free-regular-svg-icons'


class Post extends Component {
    state = {
        post: '',
        loading: true
    };

    componentDidMount(){        
        const id = this.props.match.params.id;
        this.getPost(id)
    }

    getPost(id){
        axiosInstance.get(`http://localhost:8000/api/posts/${id}`)
        .then(res => {
            this.setState({
                post: res.data,
                loading: false
            });
            console.log(this.state.post)
        })
    }

  formateDate(str){
        let date = new Date(str);
        return date.getHours() + ':' + date.getMinutes() + ' Â· ' + 
               date.getFullYear()+ '-' + (date.getMonth()+1) + '-' + date.getDate();   
    }

    getRandomInt(max) {
        return Math.floor(Math.random() * Math.floor(max));
      }

    render(){
        let post = this.state.loading ? <Loader /> : 
            <article className="posts-container p-3">
                <div className="d-flex">
                    <img className="rounded-circle" src={this.state.post.user.img} height="50" width="50"></img>
                    <div className="ml-2">
                        <p className="m-0"><b>{this.state.post.user.user.first_name} {this.state.post.user.user.last_name}</b></p>
                        <p style={{color:'#5b7083'}} className="m-0">@{this.state.post.user.user.username}</p>
                    </div>
                </div>
                <div>
                    <p style={{fontSize: "18pt"}} className="mt-2 mb-1" >{this.state.post.body}</p>
                    <p style={{color:'#5b7083', fontSize: "12pt"}}> {this.formateDate(this.state.post.date)}</p>
                    <hr />
                    <p  style={{color: "#5b7083"}}> 
                        <span><b style={{color: "#212529"}}>{this.state.post.likes.length}</b> likes</span> 
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.state.post.dislikes.length}</b> dislikes</span>
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.getRandomInt(1000)}</b> comments</span>
                        &nbsp;|&nbsp;
                        <span><b style={{color: "#212529"}}>{this.getRandomInt(100)}</b> reposts</span>
                    </p>
                    <hr />
                    <div>
                    <span>className="like-span">ÐÐ´Ð¾Ð±ÑÑÑ <FontAwesomeIcon name="like" icon={faThumbsUp}/></span>
                    </div>
                </div>
            </article>
        return(
        <Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-7">
                        <h5 className="header">Post</h5>
                        {post}
                    </div>
                </div>
            </div>
        </Fragment>
    )
  }
}

export default Post
