import React,  {Component, Fragment} from 'react'
import PostsList from '../components/PostsList'
import Loader from '../Loader'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faImage } from '@fortawesome/free-solid-svg-icons'
import { faEdit } from '@fortawesome/free-regular-svg-icons'
import axiosInstance from '../axios'
import { NavLink } from 'react-router-dom';

class Repost extends Component{
    constructor(props) {
      super(props);
      this.state = {
        loading_post: true,
        symbols: 300
      };
    }

    componentDidMount(){        
        const id = this.props.match.params.id;
        this.getCurrentUser();
        this.getPost(id);
    }

    getCurrentUser(){
        axiosInstance
            .get('http://localhost:8000/api/current_user/')
            .then(res => {
                this.setState({
                account: res.data
                });
            });
        }

    getPost(id){
        axiosInstance.get(`http://localhost:8000/api/posts/${id}`)
        .then(res => {
            this.setState({
                post: res.data,
                loading_post: false,
            });
        })
    }

    render(){
        let header = this.state.loading_post ? <Loader /> : 
            <span style={{color:"gray", fontSize:"12pt", fontWeight: "normal"}}>
                Publish repost from <NavLink to={"/user/" + this.state.post.user.user.username}> @{this.state.post.user.user.username}</NavLink>
            </span>
        return(
            <Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-7">
                        <h5 className="header">Repost
                            <br />
                            {header}
                        </h5>
                        <div className="home-container">
                            <h6 style={{color: 'gray'}}>Leave a comment! <span style={{color:"#66b0ff"}}>{this.state.symbols}</span></h6>
                            <form onSubmit={this.handleSubmit}>
                                <textarea autoFocus="true" className="text-area w-100" rows="3"  maxLength="300"  placeholder="What do you think?" name="body"></textarea>
                                <div className="d-flex align-items-baseline justify-content-between mt-1">
                                    <div className="m-0 p-0">
                                        <FontAwesomeIcon className="awesome-icon" icon={faImage} color="#007bff" size="lg" />                                   
                                    </div>
                                    <div>
                                        <button type='submit' className="def-btn btn-normal">Comment</button>
                                    </div>
                                </div>
                            </form> 

                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
        )
    }
}

export default Repost
