import React, {Component } from 'react'
import PropTypes from 'prop-types'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { faThumbsUp, faThumbsDown} from '@fortawesome/free-regular-svg-icons'

class PostItem extends Component{
    constructor(props) {
      super(props);
      this.state = {

      };
      this.post = props.post;
    }

    formatDate(date){
      let dateObj = new Date(date)
      let dateNow = new Date();
      let timeDiff = Math.abs(dateNow.getTime() - dateObj.getTime())/1000;
      let add;
      if (timeDiff < 60)
        add = ' seconds'
      else if (timeDiff < 60 * 60){
        add = ' minutes'
        timeDiff /= 60
      }
      else if (timeDiff < 60 * 60 * 24){
        add = ' hours'
        timeDiff /= 60 * 24
      }
      else if (timeDiff < 60 * 60 * 24 * 30){
        add = ' days'
        timeDiff /= 60 * 60 * 24
      }

      let res = add ? Math.round(timeDiff) + add : 'More than month ago' 
      return res
    }

    getRandomInt(max) {
      return Math.floor(Math.random() * Math.floor(max));
    }

    render(){
    return (
    <article className="post-container">
      <div className="d-inline-flex flex-wrap">
        <p className="m-0"><b>{this.post.user.first_name} {this.post.user.last_name}</b></p>
        <p className="m-0" style={{color:"gray"}}>&nbsp;@{this.post.user.username}</p>
        <p className="m-0" style={{color:"gray"}}>&middot;&nbsp;{this.formatDate(this.post.date)}</p>
      </div>

      <div>{this.post.body}</div>
      <div className="d-inline-flex" style={{fontSize:"10pt"}}>
        <p className="m-0" style={{color:"#17bf63"}}>Одобряю <FontAwesomeIcon icon={faThumbsUp}/> {this.getRandomInt(10000)} </p>
        &nbsp; &nbsp; &nbsp;
        <p className="m-0" style={{color:"#e0245e"}}> Осуждаю <FontAwesomeIcon icon={faThumbsDown}  /> {this.getRandomInt(1000)}</p>
      </div>

    </article>
    )
}
}

export default PostItem
