import React, {Component } from 'react'
import PropTypes from 'prop-types'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import { faThumbsUp as likeReg, faThumbsDown as dislikeReg, faComment} from '@fortawesome/free-regular-svg-icons'
import { faReply, faThumbsUp as likeSol, faThumbsDown as dislikeSol} from '@fortawesome/free-solid-svg-icons'

class PostItem extends Component{
    constructor(props) {
      super(props);
      this.state = {
        liked: '',
        disliked: '',
        likes: null,
        dislikes: null
      };
      this.post = props.post;
      this.btnClick = this.btnClick.bind(this)
    }

    componentDidMount(){
      this.setState({
        liked: this.checkLikes(),
        disliked: this.checkDislikes(),
        likes: this.post.likes.length,
        dislikes: this.post.dislikes.length
      })
    }

    formatDate(date){
      let dateObj = new Date(date)
      let dateNow = new Date();
      let timeDiff = Math.abs(dateNow.getTime() - dateObj.getTime())/1000;
      let add;
      if (timeDiff < 60)
        add = ' seconds'
      else if (timeDiff < 60 * 60){
        add = ' minutes'
        timeDiff /= 60
      }
      else if (timeDiff < 60 * 60 * 24){
        add = ' hours'
        timeDiff /= 60 * 60
      }
      else if (timeDiff < 60 * 60 * 24 * 30){
        add = ' days'
        timeDiff /= 60 * 60 * 24
      }

      let res = add ? Math.round(timeDiff) + add : 'More than month ago' 
      return res
    }

    getRandomInt(max) {
      return Math.floor(Math.random() * Math.floor(max));
    }

    btnClick = e => {
      let element = e.target.getAttribute('name');
      switch(element){
        case 'like':
          this.setState({liked:!this.state.liked}); 
          fetch(`http://localhost:8000/api/posts/like/${this.post.id}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              user: this.props.user_id,
              liked: this.state.liked
            })
          })
          .then(response => response.json())
          .then(data => console.log(data))
          break;
        case 'dislike':
          this.setState({disliked:!this.state.disliked}); break;
      }
    }

    checkLikes(){
      let liked = false
      this.post.likes.forEach((user) => {
        if (user.id == this.props.user_id) 
          liked = true
      })
      return liked      
    }

    checkDislikes(){
      let disliked = false
      this.post.dislikes.forEach((user) => {
        if (user.id == this.props.user_id) 
          disliked = true
      })
      return disliked      
    }

    render(){
    return (
    <article className="post-container d-flex">
      <div>
        <img className="mr-2" src="https://fogsoft.com/wp-content/uploads/2017/07/user-logo.png" width="50"></img>
      </div>
      <div>
        <div className="d-inline-flex flex-wrap">
          <p className="m-0"><b>{this.post.user.first_name} {this.post.user.last_name}</b></p>
          <p className="m-0" style={{color:"gray"}}>&nbsp;@{this.post.user.username}</p>
          <p className="m-0" style={{color:"gray"}}>&nbsp;&middot;&nbsp;{this.formatDate(this.post.date)}</p>
        </div>
        <div>{this.post.body}</div>
        <div className="d-inline-flex flex-wrap" style={{fontSize:"10pt"}}>
          <p className="m-0" style={{color:"#17bf63"}}> <span name="like" onClick={this.btnClick} className="like-span">Одобряю <FontAwesomeIcon name="like" icon={this.state.liked ? likeSol : likeReg}/></span>{this.state.likes || ''} </p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#e0245e"}}> <span name="dislike" onClick={this.btnClick} className="dislike-span">Осуждаю <FontAwesomeIcon name="dislike" icon={this.state.disliked ? dislikeSol : dislikeReg}/></span>{this.state.dislikes || ''}</p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#007bff"}}> <span name="comment" onClick={this.btnClick} className="repost-span">Comment <FontAwesomeIcon name="comment" icon={faComment}/></span>{this.getRandomInt(1000)}</p>
          &nbsp;&nbsp;&nbsp;
          <p className="m-0" style={{color:"#007bff"}}> <span name="repost" onClick={this.btnClick} className="repost-span">Repost <FontAwesomeIcon name="repost" icon={faReply}/></span>{this.getRandomInt(1000)}</p>

        </div>      
      </div>  
    </article>
    )
}
}

export default PostItem
