import React,  {Component, Fragment} from 'react'
import PostsList from '../components/PostsList'
import Loader from '../Loader'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAt, faCalendar, faEnvelope, faMapMarkerAlt, faPlus, faPlusCircle, faUserAlt } from '@fortawesome/free-solid-svg-icons'
import { faEdit } from '@fortawesome/free-regular-svg-icons'

class Profile extends Component{
    constructor(props) {
      super(props);
      this.state = {
       user: '',
       posts: [],
       loading: true,
      };
    }

    componentDidMount(){
        const name = this.props.match.params.name;
        fetch(`http://localhost:8000/api/user/${name}`, {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(user => {
            this.setState({user:user});
            this.getPosts();
        })
    }

    getPosts(){
        console.log(this.state.user)
        fetch(`http://localhost:8000/api/posts/user/${this.state.user.id}`, {
        headers: {
          Authorization: `JWT ${localStorage.getItem('access_token')}`
        }
        })
        .then(res => res.json())
        .then(posts => {
            this.setState({
                posts: posts,
                loading: false
            });
        })
    }

    formateDate(str){
        let date = new Date(str);
        return date.getFullYear()+'-' + (date.getMonth()+1) + '-'+date.getDate();

    }

    render(){
        console.log("cur:", this.props.cur_user)
        return(
            <Fragment>
                <div className="container">
                    <div className="row justify-content-center">
                        <div className="col-md-10 col-lg-7">
                            <h5 className="header">{this.state.user.first_name} {this.state.user.last_name} 
                                <br /><span style={{color:"gray", fontSize:"12pt", fontWeight: "normal"}}>{this.state.posts.length} posts </span>
                            </h5>
                            <div className="pt-1 pb-1 home-container">
                           <div className="d-flex align-items-center justify-content-sm-start justify-content-center flex-wrap">
                               <div > 
                                    <img className="mr-2 align-self-center" src="https://fogsoft.com/wp-content/uploads/2017/07/user-logo.png" width="150"></img>
                               </div>
                               <div className="user-info">
                                   <p><FontAwesomeIcon size="sm" style={{color:'#4ea4ff'}} icon={faUserAlt}/> <b>{this.state.user.first_name} {this.state.user.last_name} </b></p>
                                   <p><FontAwesomeIcon size="sm" style={{color:'#4ea4ff'}} icon={faAt}/> {this.state.user.username} </p>
                                   <p><FontAwesomeIcon size="sm" style={{color:'#4ea4ff'}} icon={faEnvelope}/> {this.state.user.email} </p>
                                   <p><FontAwesomeIcon size="sm" style={{color:'#4ea4ff'}} icon={faCalendar}/> {this.formateDate(this.state.user.date_joined)} </p>
                                   <p className="btn btn-sm btn-outline-info "><FontAwesomeIcon size="sm" icon={faPlusCircle}/> Follow</p>
                               </div>
                               <div className="align-self-start ml-auto">

                               </div>
                           </div>
                           <div style={{padding: "0px 15px"}}>
                                <p className="mt-1 mb-1">Hello world!</p>
                                <p  style={{color: "gray"}}> 
                                    <span><b style={{color: "#212529"}}>0</b> followers</span> 
                                    &nbsp;
                                    <span><b style={{color: "#212529"}}>0</b> following</span>
                                </p>

                           </div>
                            <h6 style={{padding: "0px 15px"}}>
                                Posts by&nbsp;
                                <b>{this.state.user.first_name} {this.state.user.last_name}</b>&nbsp;
                                <span style={{color: "gray"}}>@{this.state.user.username}</span>
                            </h6>
                            </div>

                            <PostsList user_id={this.props.cur_user} posts={this.state.posts}></PostsList>
                            {this.state.loading && <Loader />}
                        </div>
                    </div>
                </div>
            </Fragment>
        )
        }
    }

export default Profile;
