import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';
import ReactDOM from 'react-dom'
import React, { Component, Fragment } from 'react';
import axiosInstance from '../axios';
import Loader from '../Loader'

class Post extends Component {
  state = {
    post: '',
    loading: true
  };

  componentDidMount(){        
    const id = this.props.match.params.id;
    this.getPost(id)
  }

  getPost(id){
    axiosInstance.get(`http://localhost:8000/api/posts/${id}`)
    .then(res => {
        this.setState({
            post: res.data,
            loading: false
        });
        console.log(this.state.post)
    })
  }

  render(){
        let post = this.state.loading ? <Loader /> : 
            <article className="posts-container p-3">
                <div className="d-flex">
                    <img className="rounded-circle" src={this.state.post.user.img} height="50" width="50"></img>
                    <div>
                        <p><b>{this.state.post.user.user.first_name} {this.state.post.user.user.last_name}</b></p>
                        <p>{this.state.post.user.user.username}</p>
                    </div>
                </div>
            </article>
        return(
        <Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-10 col-lg-7">
                        <h5 className="header">Post</h5>
                        {post}
                    </div>
                </div>
            </div>
        </Fragment>
    )
  }
}

export default Post
